package com.example.tugas_apk_pupu_4m.ui.home

import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.TextView
import androidx.fragment.app.Fragment
import androidx.lifecycle.ViewModelProvider
import androidx.recyclerview.widget.GridLayoutManager
import com.example.tugas_apk_pupu_4m.Program
import com.example.tugas_apk_pupu_4m.core.ProgramAdapter
import com.example.tugas_apk_pupu_4m.databinding.FragmentHomeBinding

class HomeFragment : Fragment() {

    private var _binding: FragmentHomeBinding? = null

    private val binding get() = _binding!!

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?, savedInstanceState: Bundle?
    ): View {
        _binding = FragmentHomeBinding.inflate(inflater, container, false)
        initiateRecyclerView()
        return binding.root
    }

    fun initiateRecyclerView() {
        val listAllProgram = listOf(
            Program(
                image = "data:image/jpeg;base64,",
                name = "python",
                description = "deskripsi Python"
            ), Program(
                image = "data:image/png;base64,",
                name = "Java",
                description = "deskripsi Java"
            ), Program(
                image = "data:image/png;base64,",
                name = "Flutter",
                description = "deskripsi flutter"
            ), Program(
                image = "data:image/png;base64,",
                name = "Golang",
                description = "deskripsi Golang"
            ), Program(
                image = "data:image/png;base64,",
                name = "Mysql",
                description = "deskripsi Mysql"
            ), Program(
                image = "data:image/png;base64,",
                name = "PHP",
                description = "deskripsi PHP"
            ), Program(
                image = "data:image/png;base64,",
                name = "Node.js",
                description = "deskripsi Node.Js"
            ), Program(
                image = "data:image/png;base64,",
                name = "JavaScript",
                description = "deskripsi JavaScript"
            )
        )

        val adapterProgram = ProgramAdapter(listAllProgram)

        binding.recyclerViewAllProgram.apply {
            binding.recyclerViewAllProgram.layoutManager = GridLayoutManager(requireContext(), 2)
            adapter = adapterProgram
        }
    }

    override fun onDestroyView() {
        super.onDestroyView()
        _binding = null
    }
}